import React from "react";
import { useParams, useNavigate } from "react-router-dom";

const Color = ({ selectColor }) => {
  const { color } = useParams();

  const navigate = useNavigate();

  const colorNew = selectColor.filter((item) => item.colorName === color)[0];

  return (
    <div style={{ backgroundColor: `${colorNew.colorValue}` }} className="New">
      <div className="">
        <p>THIS IS {colorNew.colorName.toUpperCase()}</p>
        <p className="Opinion">ISNT IT BEAUTIFUL ?</p>
        <button className="underlined" onClick={() => navigate("/")}>
          GO BACK
        </button>
      </div>
    </div>
  );
};

export default Color;
