import { Routes, Route } from "react-router-dom";
import Addcolor from "./components/AddNewColor";
import Home from "./components/HomePage";
import Color from "./components/SingleColor";
import { useState } from "react";

function Colors() {
  const [selectColor, setSelectColor] = useState([
    { colorName: "red", colorValue: "#FF0000" },
    { colorName: "green", colorValue: "#0000FF" },
    { colorName: "blue", colorValue: "00FF00" },
  ]);

  return (
    <Routes>
      <Route path="/" element={<Home selectColor={selectColor} />} />
      <Route
        path="/colors/new"
        element={
          <Addcolor selectColor={selectColor} setSelectColor={setSelectColor} />
        }
      />
      <Route
        path="/colors/:color"
        element={<Color selectColor={selectColor} />}
      />
    </Routes>
  );
}

export default Colors;
